import { Component, OnInit } from '@angular/core';
import { CartService } from 'src/app/service/cart.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})

export class HeaderComponent implements OnInit {
  public totalItem:number = 0
constructor(private cartService:CartService){}
ngOnInit():void{
  this.cartService.getProducts()
  .subscribe(res => {
    this.totalItem = res.length
  })
}

}
<header>
  <div class="navbar navbar-dark bg-success shadow-sm">
    <div class="navbar-brand align-item-center d-flex">
      <i style="font-size: 20px; cursor: pointer; margin-left: 10px;" class="fas fa-shipping-fast" routerLink="/">
        K-stores
      </i>
      <div class="form-group">
        <input type="text"  class="form-control" placeholder="Search products" />
      </div>
      <span class="fas fa-search search-icon"></span>
    </div>
    <div>
      <button routerLink="cart" class="btn btn-primary">
        <i style="font-size: 80px;" class="fas fa-cart-plus" style="color:whitesmoke">
        </i>
        <div class="badge bg-danger">{{totalItem}}</div>
      </button>
    </div>
  </div>
</header>
