import { Component, Input, Output, EventEmitter } from '@angular/core';
import {  ActivatedRoute, ParamMap } from '@angular/router';
import {CheckoutProduct} from '../../models/Models'

interface OnInit {
  ngOnInit(): void
}

@Component({
  selector: 'app-cart',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})


export class CheckoutComponent implements OnInit {


  @Input() item = '';
  @Input() product = {}

  title:string = 'Cart'
  productOrder:CheckoutComponent[] = []

  id:string = ''
  fullname:string = ''
  address:string = ''
  cardNumber:string = ''

constructor(private route:ActivatedRoute){}
@Output() addForm: EventEmitter<any> = new EventEmitter()
ngOnInit(): void {
  this.id = this.route.snapshot.params['id'];
}
formSubmit():void{
  const formData = {
    fullname:this.fullname,
    address: this.address,
    carNumber:this.cardNumber
  }
  if(formData.fullname === ''){
    alert("Please enter your fullname")
  }  if(formData.address === ''){
    alert("Please enter your address")
  }
  if(formData.carNumber === ''){
    alert("Please enter your card number")
  }
  this.addForm.emit(formData)
  this.fullname = ''
  this.address = ''
  this.cardNumber = ''
}
}

<div>
  <h5 style="text-align: center; position: relative; top:70px;">Complete your order</h5>

  <div class="container">
      <form class="form-group">
        <div><label for="fullname">Full name</label></div>
        <input class="input-group" type="text" name="fullname" placeholder="Enter fullname" style="width: 300px;"/>
        <div><label for="address">Address</label></div>
        <textarea style="width: 300px;" class="input-group" type="text" name="address" placeholder="Enter billiing address"></textarea>
        <div><label for="carn number">Credit card number</label></div>
        <div><input style="width: 300px;" class="input-group" type="text" name="creditcardnumber" placeholder="Enter Credit card number"/></div>
        <input type="submit" class="btn btn-success" style="width:300px; margin: 10px 0;" value="Submit"/>
      </form>
    </div>
</div>


