import { Component, OnInit, Input } from '@angular/core';
import { Router, ActivatedRoute, ParamMap } from '@angular/router';
import { Observable } from 'rxjs';
import { switchMap, map } from 'rxjs/operators';
import { CartService } from 'src/app/service/cart.service';
import {ProductsService} from '../../service/products.service'
import {Products} from '../../models/Models'

@Component({
  selector: 'app-products',
  templateUrl: './products.component.html',
  styleUrls: ['./products.component.css']
})
export class ProductsComponent {
  title:string =  'Products Page'
  products:Products[] = []



  constructor(private productsData:ProductsService,  private route: ActivatedRoute, private cartService:CartService){
    const id: Observable<string> = route.params.pipe(map(p => p['id']));
  }

  addToCart(item:any):void{
    this.cartService.addToCart(item)
  }
  addToWishList(wish:string){
  }


  ngOnInit():void{

    this.productsData.getProducts().subscribe(res => {
       this.products = res;

       this.products.forEach((a:any) => {
        Object.assign(a, {quantity:1, total:a.price} )
       })

      })
  }

}
<div class="loader" *ngIf="products.length === 0">
<h1>Loading...</h1>
</div>
<div class="container" style="display: flex;" >
  <div class="row">
    <ng-container  *ngFor="let product of products" style="margin:0 5px;">
    <div class="card col-md-3" style="cursor: pointer">
      <img src={{product.image}} alt="Product image" width="200px" />
      <h5 style="white-space: nowrap; overflow: hidden; text-overflow: ellipsis; max-width: 100ch;">{{product.title}}</h5>
      <p>N{{product.price}}</p>
      <p>{{product.category}}</p>
      <p>{{product.description}}</p>
      <button class="btn btn-success" (click)="addToCart(product)">Add to cart</button>
    </div>
  </ng-container>

  </div>
</div>
