import { Component, Input, Output, EventEmitter } from '@angular/core';
import {  ActivatedRoute, ParamMap } from '@angular/router';

interface OnInit {
  ngOnInit(): void
}
interface Product {
  image:string;
  price:number;
  title:string;
  amount?:number;
}
@Component({
  selector: 'app-cart',
  templateUrl: './checkout.component.html',
  styleUrls: ['./checkout.component.css']
})


export class CheckoutComponent implements OnInit {

  fullname:string = ''
  address:string = ''
  cardNumber:string = ''

  @Input() item = '';
  // @Input() product = {}

title:string = 'Cart'
productOrder:Product[] = []
id:string = ''

constructor(private route:ActivatedRoute){}
@Output() addForm: EventEmitter<any> = new EventEmitter()
ngOnInit(): void {
  this.id = this.route.snapshot.params['id'];
}
formSubmit():void{
  const formData = {
    fullname:this.fullname,
    address: this.address,
    carNumber:this.cardNumber
  }
  if(formData.fullname === ''){
    alert("Please enter your fullname")
  }  if(formData.address === ''){
    alert("Please enter your address")
  }
  if(formData.carNumber === ''){
    alert("Please enter your card number")
  }
  console.log(formData, 'data')
  this.addForm.emit(formData)
  this.fullname = ''
  this.address = ''
  this.cardNumber = ''
}
}

<div class="container">

  <div class="form">
    <form >
      <div><label for="fullname">Full name</label></div>
      <div><input class="input-group" type="text" name="fullname" placeholder="Enter fullname" [(ngModel)]="fullname" /></div>
      <div><label for="address">Address</label></div>
      <div><textarea class="input-group" type="text" name="address" placeholder="Enter billiing address" [(ngModel)]="address" ></textarea></div>
      <div><label for="carn number">Credit card number</label></div>
      <div><input class="input-group" type="text" name="creditcardnumber" placeholder="Enter Credit card number" [(ngModel)]="cardNumber"/></div>
      <input type="submit" class="btn btn-outline-primary" style="width:inherit; margin: 10px 0;" value="Submit"/>
    </form>
  </div>
</div>
